U0 UpdateWin(TaskStruct *task)
{
  U8 buf[128];
  I64 i;
  GrDC *dc=GrDCAlias(gr_dc,task);

//<CTLR-T> to see the syntax of the
//"Insert Binary" statement in the following.
  for (i=0;i<NUM_TREES;i++)
    GrSpritePlot3(dc,trees_x[i],trees_y[i]-cur_screen_y,0,__BIN_2);

  dc->collision_cnt=0;
  dc->bkcolor=ROP_COLLISION+WHITE;
  GrSpritePlot3(dc,x,y,0,__BIN_1);
  if (dc->collision_cnt) {
    Snd(400);
    penalty++;
  } else
    Snd(0);

  dc->bkcolor=ROP_EQU;
  GrSpritePlot3(dc,x,y,0,__BIN_1);
  GrDCDel(dc);

//Text attr include forground in the
//lowest 4 bits and background in the upper 4.
  SPrintF(buf,"Penalty:%d",penalty);
  PlotWinStr(task,buf,BLACK+WHITE*16,1,1);
}

