	    if (x>=GR_WIDTH) x-=GR_WIDTH;
	    break;
	  case SC_CURSOR_LEFT:
	    x-=10;
	    if (x<0) x+=GR_WIDTH;
	    break;
	  case SC_CURSOR_UP:
	    y-=10;
	    if (y<0) y+=GR_HEIGHT;
	    break;
	  case SC_CURSOR_DOWN:
	    y+=10;
	    if (y>=GR_HEIGHT) y-=GR_HEIGHT;
